const TodoInput = () => {
	return (
		<div className="radio-box">
			<label key={option.value} className="radio-label">
				<input
					type="radio"
					name="radio-option"
					value={option.value}
					checked={selectedOption === option.value}
					onChange={onChange}
				/>
				{option.label}
			</label>
		</div>
	);
};
export default TodoInput;
